import React, { createContext, useReducer } from "react";
import AppReducer from "./AppReducer";

const initialState = {
  cart: [],
  orders: [],
};

export const GlobalContext = createContext(initialState);

export const GlobalProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);

  // Add item to cart (prevents duplicates)
  const addItemToCartList = (item) => {
    if (!state.cart.some((cartItem) => cartItem.id === item.id)) {
      dispatch({
        type: "ADD_ITEM_IN_CART",
        payload: item,
      });
    }
  };

  // Remove item from cart
  const removeItemFromCartList = (itemId) => {
    dispatch({
      type: "REMOVE_ITEM_IN_CART",
      payload: itemId,
    });
  };

  // Clear entire cart
  const clearCart = () => {
    dispatch({
      type: "CLEAR_CART",
    });
  };

  // Add new order (includes cart items + order ID)
  const addOrder = () => {
    if (state.cart.length === 0) return;

    const newOrder = {
      orderId: Date.now(), // Unique order ID based on timestamp
      items: [...state.cart],
      orderDate: new Date().toLocaleDateString(),
    };

    dispatch({
      type: "ADD_ORDER",
      payload: newOrder,
    });

    clearCart(); // Empty the cart after placing an order
  };

  // Remove an entire order
  const removeOrder = (orderId) => {
    dispatch({
      type: "REMOVE_ORDER",
      payload: orderId,
    });
  };

  return (
    <GlobalContext.Provider
      value={{
        cart: state.cart,
        orders: state.orders,
        addItemToCartList,
        removeItemFromCartList,
        clearCart,
        addOrder,
        removeOrder,
      }}
    >
      {children}
    </GlobalContext.Provider>
  );
};
