import React, { useContext } from "react";
import { GlobalContext } from "../../context/GlobalState";

function Orders() {
  const { orders } = useContext(GlobalContext);

  // Function to calculate expected delivery date range
  const getExpectedDelivery = () => {
    const today = new Date();
    const minDelivery = new Date(today);
    minDelivery.setDate(today.getDate() + 3);
    const maxDelivery = new Date(today);
    maxDelivery.setDate(today.getDate() + 6);

    return `Expected delivery: ${minDelivery.toDateString()} - ${maxDelivery.toDateString()}`;
  };

  return (
    <div className="orders-container">
      <h2>Your Orders</h2>

      {orders.length === 0 ? (
        <p>No orders found. Start shopping now!</p>
      ) : (
        orders.map((order) => (
          <div className="checkout-container" key={order.orderId}>
            <h3>Order ID: #{order.orderId}</h3>
            <div className="order-items">
              {order.items.map((item) => (
                <div className="cart-item" key={item.id}>
                  <div className="item-name">{item.name}</div>
                  <div className="item-price">â‚¹{item.price}</div>
                  <div className="item-expectedDelivery">{getExpectedDelivery()}</div>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default Orders;
